INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2130','article','953','2','admin5','杨林','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/f6b2ac954a00aa75.JPG','','','','6','男，主任中医师。任江苏省中医学会肺系分会委员，盐城市中医学会副会长，盐城市中医学会内科分会副主任委员，是盐城市名中医、盐城市名老中医专家学术经验继承指导老师。从医40余年，专攻中医内科，发明了“清胆合剂”、“残石净合剂”，对系统性红斑狼疮、慢性肾炎、紫癜性肾炎的治疗效果显著。获市科技进步一等奖1项；分别获盐城市政府、江苏省中医药科技进步二等奖3项。在省级以上杂志发表论文80余篇。','','','','','','','','','1','admin5','0','1626233318','1626233318','1626233318','1626233318','1','','','881','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','血栓闭塞性脉管炎、下肢静脉血栓形成、慢性肾炎、紫癜性肾炎、系统性红斑狼疮、术后肝内胆管结石等疑难病的治疗','','','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2131','article','953','2','admin5','徐晓军','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/d7faffb0ccbc83ae.JPG','','','','6','男，本科学历，主任中医师。主要从事中西医结合治疗老年病及内科疑难病（如高血压、脑卒中、冠心病、慢性胃肠炎、2型糖尿病等）的研究工作。在省级以上刊物发表学术论文7篇，参加《剔瘀通络法治疗下肢深静脉血栓的临床研究》的科研工作，获盐城市2011年度科技进步二等奖。','','','','','','','','','1','admin5','0','1626233366','1626233366','1626233366','1626233366','1','','','598','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','中西医结合治疗慢性消化系统疾病（如慢性胃炎、慢性肠炎、胃肠功能紊乱等）及中医辨证治疗心脑血管疾病（如高血压病、脑卒中等）','','','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2132','article','953','2','admin5','周翠红','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/51befeb1c606eb7d.JPG','','','','6','女，本科学历，主任中医师。主要从事中西医结合治疗老年病及内科疑难病（如脑梗塞、冠心病、2型糖尿病、紫癜性肾炎、原发性血小板减少症、系统性红色斑狼疮等）的研究工作。在省级以上刊物发表学术论文10篇，参加《重叠疏凿疗法治疗血栓闭塞性脉管炎的临床研究》的科研工作，获盐城市2007年度科技进步一等奖。','','','','','','','','','1','admin5','0','1626233405','1626233405','1626233405','1626233405','1','','','615','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','中西医结合治疗周围血管病（如血栓闭塞性脉管炎、下肢深静脉血栓形成、动脉硬化性闭塞症等）','','','','','','');
