REPLACE INTO `p8_cms_item_article_addon` (`id`, `iid`, `page`, `addon_title`, `addon_frame`, `addon_summary`, `ip`, `last_update_ip`, `timestamp`, `content`, `vote_id`) VALUES(1029, 1362, 1, '', '<!--#p8_attach#-->/others/jpg/site21/20170327/b083fe7b41a41a4352c51c.jpg', '3月27日，武警张掖市森林支队某中队战士为红湾小学学生演示发生火灾逃生办法。3月27日，武警张掖市森林支队某中队指导员张国黄为红湾小学师生讲解安全知识。在第22个全国中小学生安全教育日，武警张掖市森林支队官兵来到肃南裕固族自治县红湾小学，以&amp;amp;ldquo;珍爱生命，', '113.246.111.247', '113.246.111.247', 1547630215, '<p style="text-align:center"><img id="10749664" src="<!--#p8_attach#-->/others/jpg/site21/20170327/b083fe7b41a41a4352c51c.jpg" title="" /></p>\r\n\r\n<p style="text-align:center">3月27日，武警张掖市森林支队某中队战士为红湾小学学生演示发生火灾逃生办法。</p>\r\n\r\n<p style="text-align:center"><img id="10749660" src="<!--#p8_attach#-->/others/jpg/site21/20170327/b083fe7b41a41a43525c1a.jpg" title="" /></p>\r\n\r\n<p style="text-align:center">3月27日，武警张掖市森林支队某中队指导员张国黄为红湾小学师生讲解安全知识。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在第22个全国中小学生安全教育日，武警张掖市森林支队官兵来到肃南裕固族自治县红湾小学，以&ldquo;珍爱生命，安全第一&rdquo;为主题给师生讲解火灾、地震灾害的应急避险与自护自救知识，并组织演练活动，提高师生应急避险与安全自救能力。（张小军 程佳兴）</p>\r\n\r\n<p style="text-align:center"><img id="10749662" src="<!--#p8_attach#-->/others/jpg/site21/20170327/b083fe7b41a41a4352a71b.jpg" title="" /></p>\r\n\r\n<p style="text-align:center">3月27日，红湾小学学生在课堂上与&ldquo;老师&rdquo;互动。</p>\r\n', NULL);
