REPLACE INTO `p8_cms_item_video_addon` (`id`, `iid`, `page`, `addon_title`, `addon_frame`, `addon_summary`, `ip`, `last_update_ip`, `timestamp`, `content`, `video_height`, `video_url`, `video_width`) VALUES(1, 1041, 1, '', '<!--#p8_attach#-->/cms/item/2015_05/15_20/e4b1a228de92e6e2.jpg', '中国也是世界上人口密度较高的国家之一，每平方公里的平均人口密度为134人；但分布颇不均衡。 计划生育是中国的一项基本国策', '14.19.98.108', '113.96.84.225', 1400860800, '<p><span style="color: rgb(51, 51, 51); font-family: arial; font-size: 13.90999984741211px; line-height: 26.421875px;">中国也是世界上人口密度较高的国家之一，每平方公里的平均人口密度为134人；但分布颇不均衡。&nbsp;计划生育是中国的一项基本国策</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center" style="text-align: center;">&nbsp;</p>\r\n', 390, 'http://player.youku.com/player.php/Type/Folder/Fid/22104611/Ob/1/sid/XNjkxMTc0MTAw/v.swf', 450);
REPLACE INTO `p8_cms_item_video_addon` (`id`, `iid`, `page`, `addon_title`, `addon_frame`, `addon_summary`, `ip`, `last_update_ip`, `timestamp`, `content`, `video_height`, `video_url`, `video_width`) VALUES(2, 1042, 1, '', '<!--#p8_attach#-->/cms/item/2014_04/13_15/9da4b4725d2f596e.jpg', '12月11日下午，淮南市凤台县民防办一行在县政府办副主任余茂兵同志陪同下到岗集镇参观学习人防民防工作。岗集镇党委副书记武继伟同志陪同。凤台县民防办一行首先实地考察了岗集镇民防办设施建设情况，查阅了相关制度文件，随后到岗集镇政府四楼会议室座谈。在县委、县政', '14.19.98.108', '14.19.98.108', 1400937450, '<span style="font-family: 宋体, Arial, Helvetica, sans-serif;">12月11日下午，淮南市凤台县民防办一行在县政府办副主任余茂兵同志陪同下到岗集镇参观学习人防民防工作。岗集镇党委副书记武继伟同志陪同。</span><br style="font-family: 宋体, Arial, Helvetica, sans-serif;" />\r\n<span style="font-family: 宋体, Arial, Helvetica, sans-serif;">凤台县民防办一行首先实地考察了岗集镇民防办设施建设情况，查阅了相关制度文件，随后到岗集镇政府四楼会议室座谈。在县委、县政府坚强领导下，长丰县民防人防工作取得了长足进步，余茂兵从体制建设、队伍建设、工程建设、机制创新、宣传教育等方面向客人介绍了长丰县人防民防基本情况。凤台县民防办主任在会上说，长丰县人防民防工作走在全省前列，这次来参观学习，一定把经验做法带回去，切实把人防民防工作做好。</span><br style="font-family: 宋体, Arial, Helvetica, sans-serif;" />\r\n<span style="font-family: 宋体, Arial, Helvetica, sans-serif;">随后凤台县民防办一行参观了明珠苑示范点。</span>', 390, 'http://player.youku.com/player.php/Type/Folder/Fid/22255022/Ob/1/sid/XNzE2NDcwMTg0/v.swf', 450);
