INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`)VALUES('305','1054','1','','','校党委书记：易佐永主持党委全面工作。校长：庾建设主持行政全面工作。校党委副书记：赖卫华分管组织干部、宣传、思想政治理论课工作、统战、离退休、计划生育、校友会工作。副校长：董皞分管财务、高等职业教育、成人教育、体育、中小学校长和师资培训、直属单位工作。','121.8.7.164','222.240.162.130','1408809600','<p>\r\n	<br />\r\n	<span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">校党委书记</span></strong></span><strong style=\"font-family: serif; font-size: 12px; \"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">易佐永</span></strong></span><br />\r\n	<span style=\"font-size: 12px;\">主持党委全面工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size:12px;\"><span style=\"font-size:16px;\"><strong>校长</strong></span></span><strong style=\"font-family: serif; font-size: 12px; \"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size:12px;\"><span style=\"font-size:16px;\"><strong>庾建设</strong></span></span><br />\r\n	<span style=\"font-size: 12px;\">主持行政全面工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">校党委副书记</span></strong></span><strong style=\"font-family: serif; font-size: 12px; \"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">赖卫华</span></strong></span><br />\r\n	<span style=\"font-size: 12px;\">分管组织干部、宣传、思想政治理论课工作、统战、离退休、计划生育、校友会工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size: 12px;\"><strong><span style=\"font-size:16px;\">副校长</span></strong></span><strong style=\"font-family: serif; font-size: 12px;\"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size: 12px;\"><strong><span style=\"font-size:16px;\">董皞</span></strong></span><br />\r\n	<span style=\"font-size: 12px;\">分管财务、高等职业教育、成人教育、体育、中小学校长和师资培训、直属单位工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size: 12px;\"><span style=\"font-size:16px;\"><strong>副校长</strong></span></span><strong style=\"font-family: serif; font-size: 12px;\"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size: 12px;\"><span style=\"font-size:16px;\"><strong>禹奇才</strong></span></span><br />\r\n	<span style=\"font-size: 12px;\">分管党委办公室校办公室、桂花岗校区、依法治校、信息化建设工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">副校长</span></strong></span><strong style=\"font-family: serif; font-size: 12px; \"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">陈永亨</span></strong><br />\r\n	<br />\r\n	分管人事、科技与服务地方、学报、档案管理、重点实验室建设工作。<br />\r\n	<br />\r\n	<br />\r\n	<span style=\"font-size:16px;\"><strong>纪委书记</strong></span></span><strong style=\"font-family: serif; font-size: 12px; \"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size:12px;\"><span style=\"font-size:16px;\"><strong>陈少梅</strong></span></span><br />\r\n	<span style=\"font-size: 12px;\">主持纪委全面工作，分管监察、审计、招投标管理、工会、教代会工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size: 12px;\"><span style=\"font-size:16px;\"><strong>副校长</strong></span></span><strong style=\"font-family: serif; font-size: 12px;\"><span style=\"font-size: 16px; \">：</span></strong><span style=\"font-size: 12px;\"><span style=\"font-size:16px;\"><strong>徐俊忠</strong></span></span><br />\r\n	<span style=\"font-size: 12px;\">分管研究生教育与管理、人文社会科学研究、重点学科建设工作。</span><br />\r\n	<br />\r\n	<span style=\"font-size:12px;\"><strong><span style=\"font-size:16px;\">副校长：陈爽</span></strong></span><br />\r\n	<span style=\"font-size: 12px;\">分管学生思想政治教育与管理、共青团、学生社团、校园文化活动、国际交流与合作、实验室与设备管理工作。&nbsp;</span><br />\r\n	<br />\r\n	<strong><span style=\"font-size: 16px; \">校长助理：</span>邓成明 &nbsp;周云 &nbsp;傅继阳</strong><br />\r\n	<span style=\"font-family: serif; font-size: 12px;\">全面辅助校长各项工作。</span></p>