INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`)VALUES('568','1362','1','','<!--#p8_attach#-->/cms/item/2020_02/26_22/7bc0c0f04abca1d8.jpg','为“艾”发光 共建健康中国第32个世界艾滋病日到来之际，11月29日晚，学生举办为“艾”发光活动，用烛光点亮“NO AIDS”和红丝带的字样和图案，呼吁广大青年知艾防艾，共享健康。','113.218.174.87','113.218.174.87','1586767761','<p style=\";text-indent:32px;text-autospace:ideograph-numeric;text-align:center;line-height:33px\"><strong><span style=\"font-family: 微软雅黑;font-size: 16px\"><span style=\"font-family:微软雅黑\">为</span>&ldquo;艾&rdquo;发光 共建健康中国</span></strong></p>\r\n\r\n<p style=\"text-indent: 32px; line-height: 33px; text-align: center;\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_16/20371e3143d36278.jpg\" target=\"_blank\"><img alt=\"1575114315355.jpg\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_16/20371e3143d36278.jpg\" title=\"3.jpg\" /></a><span style=\"font-family: 微软雅黑;\">&nbsp;</span></p>\r\n\r\n<p style=\"text-indent: 32px; line-height: 33px; text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\";text-indent: 28px;line-height: 33px\"><span style=\";font-family:微软雅黑;font-size:14px\"><span style=\"font-family:微软雅黑\">本网讯（通讯员刘攀</span> <span style=\"font-family:微软雅黑\">记者李建华）在第</span>32个世界艾滋病日到来之际，11月29日晚，学生举办为&ldquo;艾&rdquo;发光活动，用烛光点亮&ldquo;NO AIDS&rdquo;和红丝带的字样和图案，呼吁广大青年知艾防艾，共享健康。</span></p>\r\n\r\n<p style=\";text-indent: 28px;line-height: 33px\"><span style=\";font-family:微软雅黑;font-size:14px\"><span style=\"font-family:微软雅黑\">青年志愿者中心骨干大学生向在场的近</span>200名大学生讲解防艾知识，唤起防&ldquo;艾&rdquo;从自身做起的社会责任感，消除歧视和偏见，关心和帮助艾滋病患者，共担防艾责任，共享健康权益，共建健康中国。</span></p>\r\n\r\n<p style=\"text-indent: 28px; line-height: 33px; text-align: center;\"><span style=\";font-family:微软雅黑;font-size:14px\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_16/c2278f557cfe91d7.png\" target=\"_blank\"><img alt=\"1575114329416.png\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_16/c2278f557cfe91d7.png\" title=\"2.png\" /></a></span></p>\r\n\r\n<p style=\"text-indent: 43px\"><span style=\";font-family:宋体;font-size:21px\">&nbsp;</span></p>\r\n\r\n<p style=\"text-indent:43px;text-align:justify;text-justify:inter-ideograph\"><span style=\";font-family:Calibri;font-size:21px\">&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n','','','','','');
