INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`)VALUES('570','1364','1','','<!--#p8_attach#-->/cms/item/2020_02/26_22/d57883c29273e0f4.jpg','我和我的祖国”全校师生大合唱比赛开我和我的祖国”全校师生大合唱比赛开赛我和我的祖国”全校师生大合唱比赛开赛赛','113.218.174.87','113.218.174.87','1586767761','<div class=\"wp_articlecontent\">\r\n<p style=\"text-align:center\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/72687b6eefb42a8f.jpg\" target=\"_blank\"><img alt=\"e0eba684-f555-496f-a89f-c4046821ea98.jpg\" data-layer=\"photo\" original-src=\"/_upload/article/images/de/78/2cff74594bf0a709690aa671d186/e0eba684-f555-496f-a89f-c4046821ea98_d.jpg\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/72687b6eefb42a8f.jpg.cthumb.jpg\" sudyfile-attr=\"{\'title\':\'3.jpg\'}\" /></a></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/2af08f5b0efe88c0.jpg\" target=\"_blank\"><img alt=\"0bf61230-381f-4562-92a4-5ecbeb75ce78.jpg\" data-layer=\"photo\" original-src=\"/_upload/article/images/de/78/2cff74594bf0a709690aa671d186/df4e4fcf-6364-44f2-8f67-490e2dee6d54_d.jpg\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/2af08f5b0efe88c0.jpg.cthumb.jpg\" sudyfile-attr=\"{\'title\':\'2.jpg\'}\" /></a></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-indent:2em;text-align:justify;line-height:2em;\"><span style=\"text-indent:32px;font-size:16px;font-family:宋体, simsun;line-height:2em;\">&ldquo;我和我的祖国，一刻也不能分割，无论我走到哪里，都流出一首赞歌&hellip;&hellip;&rdquo;6月11日晚，剧场回荡着《我和我的祖国》的响亮歌声，来自全校54个院级工会的4000余名师生，用歌声献礼中华人民共和国70华诞。&ldquo;我和我的祖国&rdquo;全校师生大合唱比赛现场，师生们通过歌声、朗诵、独唱、舞蹈等形式把自己对祖国的爱&ldquo;尽情诉说&rdquo;。</span></p>\r\n\r\n<p style=\"text-indent:2em;text-align:justify;font-size:16px;font-family:宋体, simsun;line-height:2em;\">70年风雨兼程，70年砥砺奋进，中华儿女勠力同心；70年春华秋实，70年薪火相传，铸就今日辉煌中国。师生们唱出中国梦，唱响爱国情，用歌声讲述国家富强、民族振兴、人民幸福的今日中国。唱响主旋律、讴歌新时代，师生们把对祖国的热爱融入到美妙的歌声中，为扎根中国大地加快推进&ldquo;双一流&rdquo;建设，迈向世界一流大学前列汇聚磅礴力量。</p>\r\n\r\n<p style=\"text-indent:2em;text-align:justify;font-size:16px;font-family:宋体, simsun;line-height:2em;\">作为材料科学与工程学院的领唱，张泽院士引吭高歌，用一曲《祖国不会忘记》表达对祖国的深情厚谊；医学院的一曲《东方之珠》讲述了百年来求是文脉的滋养；医学院附属第二医院的一曲《我为共产主义把青春贡献》唱出了六千多名员工坚持&ldquo;患者与服务对象至上&rdquo;的理念；管理学院在众多自选曲目之中另辟蹊径，用一曲《游子情思》表达归国管理人在异国他乡求学深造之时的思乡之情&hellip;&hellip;</p>\r\n\r\n<p style=\"text-indent:2em;text-align:justify;font-size:16px;font-family:宋体, simsun;line-height:2em;\">在中华人民共和国成立70周年之际，浙大师生以大合唱的方式，展示了浙大师生斗志昂扬、奋发有为、蓬勃向上的新时代精神风貌，进一步激发了爱国热情，增强了民族自信心和自豪感。各个参赛队伍用真情的歌声谱写了对祖国母亲真挚的告白。本次比赛由校工会主办，校党委宣传部、公共体育与艺术部、校团委协办，预赛为期三天，决赛将于9月27日举行。</p>\r\n</div>\r\n','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`)VALUES('571','1365','1','','<!--#p8_attach#-->/cms/item/2020_02/26_17/0f0d592f1856380b.jpg.thumb.jpg','学生优秀科技作品展在浙大紫金港校区展出。该展览汇集了浙江各大高校近年来在国内外各大科技竞赛中的优秀获奖作品，这些作品展现了近年来大学生','113.218.174.87','113.218.174.87','1586767761','<div class=\"wp_articlecontent\">\r\n<p style=\"text-align: center\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/128a5cf0215becb5.jpg\" target=\"_blank\"><img alt=\"f66c6fad-0c62-4630-bc8a-cf35a1b92852.jpg\" data-layer=\"photo\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/128a5cf0215becb5.jpg\" sudyfile-attr=\"{\'title\':\'A33E7714-1.jpg\'}\" /></a></p>\r\n\r\n<p style=\"text-align: center\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/097809f5e355fd89.jpg\" target=\"_blank\"><img alt=\"a0e784e3-aa14-4f59-85d8-b7b08e46b55b.jpg\" data-layer=\"photo\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/097809f5e355fd89.jpg\" sudyfile-attr=\"{\'title\':\'A33E7716-2.jpg\'}\" /></a></p>\r\n\r\n<p style=\"text-align: center\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center\"><a href=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/37ff5e80d0a37ee9.jpg\" target=\"_blank\"><img alt=\"9e3a005e-a04c-4bb4-844e-1afb943450d7.jpg\" data-layer=\"photo\" src=\"<!--#p8_attach#-->/cms/item/2020_02/26_17/37ff5e80d0a37ee9.jpg\" sudyfile-attr=\"{\'title\':\'A33E7747-3.jpg\'}\" /></a></p>\r\n\r\n<p style=\"text-align: left; line-height: 2em; text-indent: 2em\">&nbsp;</p>\r\n\r\n<p style=\"text-align: left; line-height: 2em; text-indent: 2em\"><span lang=\"EN-US\" style=\"font-size: 16px; font-family: 宋体,simsun; line-height: 2em\">5</span><span style=\"font-size: 16px; font-family: 宋体,simsun; line-height: 2em\">月</span><span lang=\"EN-US\" style=\"font-size: 16px; font-family: 宋体,simsun; line-height: 2em\">27</span><span style=\"font-size: 16px; font-family: 宋体,simsun; line-height: 2em\">日，学生优秀科技作品展在浙大紫金港校区展出。该展览汇集了浙江各大高校近年来在国内外各大科技竞赛中的优秀获奖作品，这些作品展现了近年来大学生依托高校丰富的资源平台，主动开展创新学习、创新研讨、创新实践的丰富内容，展示了当代大学生勇攀科技新高峰、开拓科技新领域的创新成果。</span></p>\r\n\r\n<p style=\"font-size: 16px; font-family: 宋体,simsun; text-align: left; line-height: 2em; text-indent: 2em\">（文 者也／摄影 古越）</p>\r\n</div>\r\n','','','','','');
