INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`)VALUES('7','1312','1','','<!--#p8_attach#-->/cms/item/2020_04/12_09/282163366352d397.jpg','123','113.246.110.81','113.247.23.222','1565937875','<p><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2001年1月，与深圳市人民政府签署《合作创办北协议书》，共同创办院。经过十五年发展，研究生院依托立足深圳，逐步成为扎根深圳的大学研究型国际化校区，大学创建世界一流大学战略的重要组成部分。</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 依托大学学科优势，结合深圳的区位优势，深圳研究生院以&ldquo;前沿领域、交叉学科、应用学术、国际标准&rdquo;为办学方针，加强学科建设。现有信息工程学院、化学生物学与生物技术学院、环境与能源学院、城市规划与设计学院、新材料学院、汇丰商学院、国际法学院以及人文社会科学学院等八个学院，学科专业涉及信息科学与技术、电子与通讯技术、化学生物学、环境科学、环境与能源、城市与区域规划、景观设计学、社会学、心理学、新闻传播、金融、经济、管理、法律等领域。</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;2001年1月，大学与深圳市人民政府签署《合作创办大学深圳校区协议书》，共同创办研究生院。经过十五年发展，研究生院依托、立足深圳，逐步成为扎根深圳的大学研究型国际化校区，大学创建世界一流大学战略的重要组成部分。</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 依托大学学科优势，结合深圳的区位优势，研究生院以&ldquo;前沿领域、交叉学科、应用学术、国际标准&rdquo;为办学方针，加强学科建设。现有信息工程学院、化学生物学与生物技术学院、环境与能源学院、城市规划与设计学院、新材料学院、汇丰商学院、国际法学院以及人文社会科学学院等八个学院，学科专业涉及信息科学与技术、电子与通讯技术、化学生物学、环境科学、环境与能源、城市与区域规划、景观设计学、社会学、心理学、新闻传播、金融、经济、管理、法律等领域。</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 依托大学学科优势，结合深圳的区位优势，深圳研究生院以&ldquo;前沿领域、交叉学科、应用学术、国际标准&rdquo;为办学方针，加强学科建设。现有信息工程学院、化学生物学与生物技术学院、环境与能源学院、城市规划与设计学院、新材料学院、汇丰商学院、国际法学院以及人文社会科学学院等八个学院，学科专业涉及信息科学与技术、电子与通讯技术、化学生物学、环境科学、环境与能源、城市与区域规划、景观设计学、社会学、心理学、新闻传播、金融、经济、管理、法律等领域。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><a href=\"<!--#p8_attach#-->/cms/item/2020_04/12_09/282163366352d397.jpg\" target=\"_blank\"><img alt=\"qinghua214.jpg\" src=\"<!--#p8_attach#-->/cms/item/2020_04/12_09/282163366352d397.jpg\" style=\"width: 766px; height: 315px;\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\