INSERT INTO `p8_special_` (`id`,`cid`,`title`,`frame`,`ifcomment`,`template`,`html_view_url_rule`,`timestamp`,`description`,`banner`,`seo_keywords`,`navigation`,`count`)VALUES('1','1','学生创业大赛专题','','0','a:3:{s:8:\"tpl_head\";s:0:\"\";s:8:\"tpl_main\";s:11:\"content_gov\";s:8:\"tpl_foot\";s:0:\"\";}','{$system_url}/special/{$id}.html','0','创业计划竞赛起源于美国，是风靡全球高校的重要赛事。它借用模拟实际企业的运作模式，要求参赛者组成优势互补的竞赛小组，提出一项具有市场前景的技术、产品或者服务，并围绕这一技术、产品或服务，完成一份完整、具体、深入的创业计划。','<!--#p8_attach#-->/core/special/2012_08/30_12/0ac2224bc87630e8.jpg','','a:8:{i:0;a:3:{s:4:\"name\";s:8:\"专题首页\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:55;}i:1;a:3:{s:4:\"name\";s:8:\"大赛简介\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:50;}i:2;a:3:{s:4:\"name\";s:8:\"赛事公告\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:48;}i:3;a:3:{s:4:\"name\";s:8:\"赛事历程\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:46;}i:4;a:3:{s:4:\"name\";s:8:\"竞技平台\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:44;}i:5;a:3:{s:4:\"name\";s:10:\"创业与就业\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:40;}i:6;a:3:{s:4:\"name\";s:8:\"大赛报名\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:35;}i:7;a:3:{s:4:\"name\";s:8:\"微博互动\";s:3:\"url\";s:0:\"\";s:5:\"order\";i:0;}}','0');
