REPLACE INTO `p8_sites_stop_data` (`id`, `cid`, `cname`, `site`, `item_id`, `sc`, `model`, `model_alias`, `title`, `link`, `data`, `timestamp`, `status`, `site_status`, `new_id`) VALUES(23, 840, '通知公告', 'school02', 8240, 'c', 'article', '文章', '中国高校毕业生数', '/s.php/school02/item-view-id-8240.html', 'a:49:{s:5:"model";s:7:"article";s:3:"cid";i:840;s:3:"uid";s:1:"1";s:8:"username";s:5:"admin";s:5:"title";s:24:"中国高校毕业生数";s:11:"title_color";s:0:"";s:10:"title_bold";s:1:"0";s:9:"sub_title";s:0:"";s:4:"site";s:8:"school02";s:5:"frame";s:57:"/attachment/sites/item/2017_05/14_10/5d20e15642bafe9d.jpg";s:3:"url";s:0:"";s:10:"attributes";s:0:"";s:7:"summary";s:170:"  政府工作报告提出，今年高校毕业生将达727万人，要开发更多就业岗位，实施不间断的就业创业服务，提高大学生就业创业比例";s:6:"source";s:9:"院系01|";s:6:"author";s:0:"";s:6:"editer";s:0:"";s:8:"keywords";s:0:"";s:8:"verified";s:1:"1";s:8:"verifier";s:1:"0";s:9:"timestamp";s:19:"2017-10-22 13:15:32";s:11:"create_time";s:10:"1508649332";s:10:"list_order";s:19:"2017-10-22 13:15:32";s:11:"update_time";s:10:"1508649332";s:5:"pages";s:1:"1";s:18:"html_view_url_rule";s:0:"";s:8:"template";s:0:"";s:5:"views";i:0;s:8:"comments";i:0;s:12:"seo_keywords";s:0:"";s:15:"seo_description";s:0:"";s:3:"iid";s:4:"8240";s:4:"page";s:1:"1";s:11:"addon_title";s:0:"";s:11:"addon_frame";s:57:"/attachment/sites/item/2017_05/14_10/5d20e15642bafe9d.jpg";s:13:"addon_summary";s:170:"  政府工作报告提出，今年高校毕业生将达727万人，要开发更多就业岗位，实施不间断的就业创业服务，提高大学生就业创业比例";s:2:"ip";s:12:"113.247.53.9";s:14:"last_update_ip";s:12:"113.247.53.9";s:7:"content";s:1562:"&nbsp; &nbsp;政府工作报告提出，今年高校毕业生将达727万人，要开发更多就业岗位，实施不间断的就业创业服务，提高大学生就业创业比例。<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;727万&rdquo;又一次触动社会的神经，相比2013年的699万增量不大但总量很大。专家分析，2014年大学生就业形势依然严峻。<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 记者梳理发现，来自教育部、人力资源和社会保障部等公布的数据显示，从2005年至2014年，10年间全国普通高校毕业生从338万人增长至727万人，总数翻了一番还要多。<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 人力资源和社会保障部国际劳动保障研究所所长莫荣说，&ldquo;我国每年新进入市场的劳动者大约1500万人，727万人差不多占到一半了。综合考虑，今年大学生就业形势不容乐观。&rdquo;莫荣说。<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 对于高校毕业生逐年增加，代表委员建议客观、理性看待。<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lsquo;难&rsquo;不应被过度放大，也不能只看数据。&rdquo;全国人大代表、共青团安徽省委书记李红认为，&ldquo;喊&lsquo;难&rsquo;的声音太多，无形中给毕业生和家长传递了一种压力感和焦虑感。&rdquo;政府工作报告明确提出开发更多就业岗位，实施不间断的就业创业服务，提高大学生就业创业比例。现在关键是将这些要求落到实处。";s:7:"item_id";s:4:"8240";s:5:"cname";s:12:"通知公告";s:11:"model_alias";s:6:"文章";s:3:"vid";i:0;s:6:"action";s:3:"add";s:2:"sc";s:1:"c";s:6:"verify";s:1:"1";s:4:"link";s:38:"/s.php/school02/item-view-id-8240.html";s:11:"source_name";s:8:"院系01";s:10:"source_url";s:0:"";s:6:"field#";a:1:{s:7:"content";R:39;}}', 1514510960, 0, '', 1247);
