<?phpdefined('PHP168_PATH') or die();/*** 更新追加内容**/if(REQUEST_METHOD == 'GET'){/*** your code**/		include template($this_module, 'addon_edit', 'admin');	}else if(REQUEST_METHOD == 'POST'){		//如果魔法引号开启strip掉	$_POST = p8_stripslashes2($_POST);		$id = isset($_POST['id']) ? intval($_POST['id']) : 0;	$id or message('no_such_item');		$this_controller->update_addon($_POST) or message('fail');		message('done', $HTTP_REFERER);}
